                writer.WriteNumber("ap_cost", weapon.ApCostOption.Value!.Value);

            if (weapon.RangeOption.IsSet)
            {
                writer.WritePropertyName("range");
                JsonSerializer.Serialize(writer, weapon.Range, jsonSerializerOptions);
            }
            if (weapon.RecipeOption.IsSet)
                if (weapon.RecipeOption.Value != null)
                {
                    writer.WritePropertyName("recipe");
                    JsonSerializer.Serialize(writer, weapon.Recipe, jsonSerializerOptions);
                }
                else
                    writer.WriteNull("recipe");
            if (weapon.ParentSetOption.IsSet)
                if (weapon.ParentSetOption.Value != null)
                {
                    writer.WritePropertyName("parent_set");
                    JsonSerializer.Serialize(writer, weapon.ParentSet, jsonSerializerOptions);
                }
                else
                    writer.WriteNull("parent_set");
        }
    }
}
